import axios from 'axios'
import aps from '../services/axios.js'
import { useState ,useEffect} from 'react'
import './Header.css'

const Home = () => {
    const [use, setUse] = useState('')

    const func = ()=>{
        aps.pritamFuncMe()
        .then(res=>{
            console.log('home res = ',res.data)
            setUse(res.data._id)
            localStorage.setItem('userId',use)
        })
    }
    useEffect(() => {
        func()
    }, [])
