    //     console.log('here is out user id')
    //     console.log(use)
    // })
    // .catch(error => {
    //     console.log('no one logged in')
    // })
    const RenderMenu = () => {
        if(use==''){
            return(
                <div >

                    <div>
                        <Loginform/>
                    </div>

                </div>
            )
        }
        else{
            return(
                <div>
                    <span><Link to={{
                     pathname: '/titleform',            
                    }} >add template</Link></span>
                    <Printmenu/> 
                </div>
            )
        }

    }
    return (
        <div>
            <RenderMenu/>
        </div>
    )
}

export default Home
