import React,{useState, useEffect} from 'react'
import aps from '../services/axios.js';
import axios from 'axios'
import Recordlayout from './Recordlayout'

const Printmenu = () => {
    const [items,setItems] = useState([])
    const [use, setUse] = useState(localStorage.getItem('userId'))

    console.log('user  = ',use)
    const loadData = async ()=>{
        const res = await aps.getTemplates()
        console.log('loaded data = ',res)
        return res.data
    }

    useEffect(() => {
        loadData()
        .then((res)=>{
            // console.log('load data success',res)
            setItems(res)
        })
    },[]) 
    const deleteTemplate = async (e,id)=>{
        e.preventDefault()
        console.log('data id = ',id)    
        await axios.deleteTemplate(id)
    }
    return (
        <div>
            <div>                
            {
                items.map(item => (            
                    <div key={item._id}>
                        <Recordlayout dataid={item._id} userid={item.userId} data={item.data} name = {item.name}/>
                        <button onClick = {(e)=>{
                            deleteTemplate(e,item._id)
                        }}>delete</button>
                    </div>

                ))
            }   
        </div>
       </div>
    )
}

export default Printmenu
